use core::cmp::Ordering;

struct Packet<'a>(&'a str);

impl PartialEq for Packet<'_> {
    fn eq(&self, other: &Self) -> bool {
        let &Packet(self_string) = self;
        let &Packet(other_string) = other;
        self_string.eq(other_string)
    }
}

impl Eq for Packet<'_> {}

impl PartialOrd for Packet<'_> {
    fn partial_cmp(&self, other: &Self) -> Option<Ordering> {
        Some(self.cmp(other))
    }
}

impl Ord for Packet<'_> {
    fn cmp(&self, other: &Self) -> Ordering {
        let Packet(line_one) = *self;
        let Packet(line_two) = *other;
        let mut packet_one = line_one.chars();
        let mut packet_two = line_two.chars();

        while let (Some(first), Some(second)) = (packet_one.next(), packet_two.next()) {
            match (first, second) {
                ('[', '[') => {
                    // first and second are both lists
                    continue;
                }
                (']', ']') => {
                    // first and second both end their current list
                    continue;
                }
                (']', _) => {
                    // first ends the list and second does not
                    return Ordering::Less;
                }
                (_, ']') => {
                    // first does not end the list and second does
                    return Ordering::Greater;
                }
                ('[', _) => {
                    // first is a list and second is a num
                    let item_b = get_num_from_string(second, packet_two.as_str());
                    let mut item_a = -1;
                    while let Some(c) = packet_one.next() {
                        match c {
                            '[' => continue,
                            ']' => return Ordering::Less,
                            _ => {
                                item_a = get_num_from_string(c, packet_one.as_str());
                                break;
                            }
                        }
                    }

                    if item_a == -1 {
                        panic!();
                    }

                    if item_a < item_b {
                        return Ordering::Less;
                    }
                    return Ordering::Greater;
                }
                (_, '[') => {
                    // first is a num and second is a list
                    let item_a = get_num_from_string(first, packet_one.as_str());
                    let mut item_b = -1;
                    while let Some(c) = packet_two.next() {
                        match c {
                            '[' => continue,
                            ']' => return Ordering::Greater,
                            _ => {
                                item_b = get_num_from_string(c, packet_two.as_str());
                                break;
                            }
                        };
                    }

                    if item_b == -1 {
                        panic!();
                    }

                    if item_b < item_a {
                        return Ordering::Greater;
                    }

                    return Ordering::Less;
                }
                (_, _) => {
                    // first and second are both nums
                    let first_num = get_num_from_string(first, packet_one.as_str());
                    let second_num = get_num_from_string(second, packet_two.as_str());

                    match first_num.cmp(&second_num) {
                        Ordering::Less => return Ordering::Less,
                        Ordering::Greater => return Ordering::Greater,
                        Ordering::Equal => {
                            let (mut x, mut y) = if let (Some(x), Some(y)) =
                                (packet_one.next(), packet_two.next())
                            {
                                (x, y)
                            } else {
                                todo!()
                            };

                            if x.is_numeric() {
                                x = packet_one.next().unwrap();
                            }

                            if y.is_numeric() {
                                y = packet_two.next().unwrap();
                            }

                            loop {
                                match (x, y) {
                                    (',', ',') => break,
                                    (']', ']') => {
                                        packet_one.next().unwrap();
                                        packet_two.next().unwrap();
                                        break;
                                    }
                                    (',', ']') => return Ordering::Greater,
                                    (']', ',') => return Ordering::Less,
                                    (_, _) => todo!(),
                                };
                            }
                        }
                    }
                }
            };
        }

        Ordering::Equal
    }
}

fn get_num_from_string(first: char, string: &str) -> i32 {
    let mut num_str = String::with_capacity(5);
    num_str.push(first);
    let string_iter = string.chars();
    for c in string_iter {
        match c.is_numeric() {
            true => num_str.push(c),
            false => break,
        }
    }

    num_str.parse::<i32>().unwrap()
}

